mapper.select:
SELECT alarmNo, alarmAct, alarmContent, alarmTime, userNo, monAct, tueAct, wedAct, thuAct, friAct, satAct, sunAct
FROM alarm
WHERE userNo = #{userNo} AND alarmNo = ${alarmNo};

SELECT alarmNo, alarmAct, alarmContent, alarmTime, userNo, monAct, tueAct, wedAct, thuAct, friAct, satAct, sunAct
FROM alarm
WHERE userNo = #{userNo};

mapper.update:
UPDATE alarm


		alarmAct = #{alarmAct}


		, alarmContent = #{alarmContent}


		, alarmTime = #{alarmTime}


		, monAct = #{monAct}


		, tueAct = #{tueAct}


		, wedAct = #{wedAct}


		, thuAct = #{thuAct}


		, friAct = #{friAct}


		, satAct = #{satAct}


		, sunAct = #{sunAct}


WHERE alarmNo =#{alarmNo};

mapper.select:
SELECT alarmAct 
FROM alarm
WHERE alarmNo = #{alarmNo};
